package main

import (
	"fmt"
	"log"
	"net"
	"net/http"
	"os"

	"github.com/go-swagger/go-swagger/spec"

	"github.com/go-swagger/go-swagger/examples/todo-list/restapi/operations"
)

// This file was generated by the swagger tool.
// Make sure not to overwrite this file after you generated it because all your edits would be lost!
// It would only be overwritten if you explicitly specify --include-main for the generate all or support commands
//go:generate swagger generate server -t ../.. -A ToDoList

func main() {
	swaggerSpec, err := spec.New(SwaggerJSON, "")
	if err != nil {
		log.Fatalln(err)
	}

	port := os.Getenv("PORT")
	if port == "" {
		port = "0"
	}

	host := os.Getenv("HOST")
	if host == "" {
		host = "localhost"
	}

	api := operations.NewToDoListAPI(swaggerSpec)
	configureAPI(api)

	listener, err := net.Listen("tcp", host+":"+port)
	if err != nil {
		log.Fatalln(err)
	}

	fmt.Printf("serving to do list at http://%s\n", listener.Addr())

	if err := http.Serve(listener, api.Serve()); err != nil {
		log.Fatalln(err)
	}
}
